import React, { Component } from 'react'

export class Categories extends Component {
  constructor(props) {
    super(props)
    this.state = {
      categories: [
        {
          key: 'all',
          name:'Все'
        },
        {
          key: 'chairs',
          name:'Стулья'
        },
        {
          key: 'tabless',
          name:'Столы'
        },
        {
          key: 'light',
          name:'Светильники'
        },
        {
          key: 'sofa',
          name:'Диваны'
        },
      ]
    }
  }
  render() {
    return (
      <div className='categories'>
        {this.state.categories.map(el => (
          <div key={el.key} onClick>{el.name}</div>
))}
      </div>
    )
  }
}

export default Categories
